package com.hmis.core.notification.persistence.dao;import java.util.List;import org.hibernate.criterion.DetachedCriteria;import com.hmis.core.notification.persistence.dao.core.ParentDao;import com.hmis.core.notification.persistence.entity.NotificationHeaderEntity;public class NotificationHeaderDao extends ParentDao implements INotificationHeaderDao{	public NotificationHeaderEntity create(NotificationHeaderEntity notificationHeaderEntity){		insert(notificationHeaderEntity);		return notificationHeaderEntity;	}	public NotificationHeaderEntity update(NotificationHeaderEntity notificationHeaderEntity){		return (NotificationHeaderEntity)update(notificationHeaderEntity);	}	public void delete(NotificationHeaderEntity notificationHeaderEntity){		delete(notificationHeaderEntity);	}	public List<NotificationHeaderEntity> read(NotificationHeaderEntity notificationHeaderEntity){		DetachedCriteria query = DetachedCriteria.forClass(NotificationHeaderEntity.class);		return (List<NotificationHeaderEntity>)findByCriteria(query);	}	public List<NotificationHeaderEntity> getNotificationHeaderByExternalId(String externalId){		DetachedCriteria query = DetachedCriteria.forClass(NotificationHeaderEntity.class);		return (List<NotificationHeaderEntity>)findByCriteria(query);	}}