package com.hmis.core.notification.persistence.dao;import java.util.List;import org.hibernate.criterion.DetachedCriteria;import com.hmis.core.notification.persistence.dao.core.ParentDao;import com.hmis.core.notification.persistence.entity.NotificationHeaderEntity;import com.hmis.core.notification.persistence.entity.NotificationLineEntity;public class NotificationLineDao extends ParentDao implements INotificationLineDao{		public NotificationLineEntity create(NotificationLineEntity notificationLineEntity){		insert(notificationLineEntity);		return notificationLineEntity;	}	public NotificationLineEntity update(NotificationLineEntity notificationLineEntity){		return (NotificationLineEntity)update(notificationLineEntity);	}	public void delete(NotificationLineEntity notificationLineEntity){		delete(notificationLineEntity);	}	public List<NotificationLineEntity> read(NotificationLineEntity notificationLineEntity){		DetachedCriteria query = DetachedCriteria.forClass(NotificationHeaderEntity.class);		return (List<NotificationLineEntity>)findByCriteria(query);	}	public List<NotificationLineEntity> getNotificationLineByExternalId(String externalId){		DetachedCriteria query = DetachedCriteria.forClass(NotificationHeaderEntity.class);		return (List<NotificationLineEntity>)findByCriteria(query);	}}