package com.hmis.core.notification.rest.endpoint.core;import javax.servlet.http.HttpServletResponse;import com.hmis.core.notification.business.exception.NotifcationException;import com.hmis.core.notification.model.ErrorResponse;public class WebserviceExceptionHandler {	Throwable throwable;	Integer httpResponseCode;		public Integer getHttpResponseCode() {		return httpResponseCode;	}	public void setHttpResponseCode(Integer httpResponseCode) {		this.httpResponseCode = httpResponseCode;	}	ErrorResponse errorResponse;	public ErrorResponse getErrorResponse() {		return errorResponse;	}	public void setErrorResponse(ErrorResponse errorResponse) {		this.errorResponse = errorResponse;	}	public WebserviceExceptionHandler(Throwable throwable) {		super();		this.throwable = throwable;	}	public Throwable getThrowable() {		return throwable;	}	public void setThrowable(Throwable throwable) {		this.throwable = throwable;	}	public void processException(){			errorResponse = new ErrorResponse();						try{					NotifcationException ne = (NotifcationException) throwable;					httpResponseCode = HttpServletResponse.SC_BAD_REQUEST;					errorResponse.setCode(ne.getErrorCode());					errorResponse.setMessage(ne.getMessage());						}catch(Exception e){						httpResponseCode = HttpServletResponse.SC_INTERNAL_SERVER_ERROR;						errorResponse.setCode("UNKNOWN_ERROR");						errorResponse.setMessage(throwable.getMessage());						throwable.printStackTrace();			}	}}